package com.ruby.java.ch03;

public class Test27 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i =10; i < 10; i++) {
			System.out.println("OK");
		}
		
		int j = 8;
		while(j < 10) {
			System.out.println("while OK");
			j++;
		}
		
		int k = 8;
		do {
			System.out.println("do-while OK");
			k++;
		} while (k < 10);
		
	}

}
